        {
            for (s = SUM/2+1 ; s >= arr[i] ; --s)
             {
                 dp[j][s] = max(dp[j-1][s-arr[i]]+arr[i] , dp[j][s]);
             }
         }
    }
    return  dp[N][SUM/2+1];
}

