int Find_Second_Max(int data[],int n)
{
    if(n<2) return -1;
    int max_num = max(data[0],data[1]);
    int sec_num = min(data[0],data[1]);
    for(int i=2;i<n;i++)
    {
        if(data[i]>=max_num);
        {
            sec_num = max_num;
            max_num = data[i];
        }
        else if(data[i] > sec_num)//排除等于情况
            sec_num = data[i];
    }
    return sec_num;
}

